create table aktion (id bigint generated by default as identity, version bigint not null, datum timestamp not null, class varchar(255) not null, anzahl integer, anzahl_kontrolltage integer, ausgebaute_waben integer, bemerkung varchar(4000), brut integer, brutwaben integer, drohnenbrut_geschnitten boolean, futterwaben integer, koenigin_gesehen boolean, mittelwaende integer, sanftmut integer, volksstaerke integer, wabensitz integer, menge decimal(19,2), sorte varchar(255), wassergehalt decimal(19,2), chargen_nummer varchar(255), methode varchar(255), mittel varchar(255), art varchar(255), primary key (id));
create table bienenstand (id bigint generated by default as identity, version bigint not null, name varchar(255) not null, standort varchar(255) not null, primary key (id));
create table bienenstand_bienenvolk (bienenstand_voelker_id bigint not null, bienenvolk_id bigint);
create table bienenvolk (id bigint generated by default as identity, version bigint not null, erstellt timestamp not null, koenigin_id bigint not null, name varchar(255) not null, primary key (id));
create table koenigin (id bigint generated by default as identity, version bigint not null, geburtsjahr integer not null, primary key (id));
create table role (id bigint generated by default as identity, version bigint not null, authority varchar(255) not null, primary key (id));
create table stand (id bigint generated by default as identity, version bigint not null, primary key (id));
create table user (id bigint generated by default as identity, version bigint not null, account_expired boolean not null, account_locked boolean not null, enabled boolean not null, "password" varchar(255) not null, password_expired boolean not null, username varchar(255) not null, primary key (id));
create table user_role (user_id bigint not null, role_id bigint not null, primary key (user_id, role_id));
